defaultpen(linewidth(.8pt)+fontsize(8pt));
dotfactor=4;

pair A=(0,0), D=8*dir(330), C=8*dir(210), B=7*dir(90);
pair Ep=(0,4-27/5);
pair[] dotted={A,B,C,D,Ep};

draw(Circle(A,10));
